import java.awt.*;import java.awt.event.KeyEvent;public class Tank {    private int x, y;    private Direction direction;    private boolean bL,bU,bR,bD;    public static final int SPEED = 25;    public Tank(int x, int y, Direction direction) {        this.x = x;        this.y = y;        this.direction = direction;    }    public void paint(Graphics g) {        g.fillRect(x, y, 50, 50);        move();    }    public void keyPressed(KeyEvent e) {        int key = e.getKeyCode();        switch (key) {            case KeyEvent.VK_LEFT:                direction = Direction.LEFT;                break;            case KeyEvent.VK_UP:                direction = Direction.UP;                break;            case KeyEvent.VK_RIGHT:                direction = Direction.RIGHT;                break;            case KeyEvent.VK_DOWN:                direction = Direction.DOWN;                break;        }        setMainDir();    }    public void keyReleased(KeyEvent e) {        int key = e.getKeyCode();        switch (key) {            case KeyEvent.VK_LEFT:                bL = false;                break;            case KeyEvent.VK_UP:                bU = false;                break;            case KeyEvent.VK_RIGHT:                bR = false;                break;            case KeyEvent.VK_DOWN:                bD = false;                break;        }        setMainDir();    }    private void setMainDir() {        if(!bL && !bR && !bU && !bD)            direction = Direction.STOP;        if(bL && !bR && !bU && !bD)            direction = Direction.LEFT;        if(!bL && bR && !bU && !bD)            direction = Direction.RIGHT;        if(!bL && !bR && bU && !bD)            direction = Direction.UP;        if(!bL && !bR && !bU && bD)            direction = Direction.DOWN;    }    public void move(){        switch (direction){            case LEFT:                x -= SPEED;                break;            case RIGHT:                x += SPEED;                break;            case UP:                y -= SPEED;                break;            case DOWN:                y += SPEED;                break;        }    }}